import React, { Component } from 'react';
import Counter from './counter';

class Counters extends Component {
    state = { 
        counters: [
            { id:1, value: 4, },
            { id:2, value: 0, },
            { id:3, value: 0, },
            { id:4, value: 0, }
        ]
     };

    handleReset = () =>{
        const counterReset = this.state.counters.map( c =>{
        c.value = 0;
        return c;
        });

        this.setState({ counterReset })
    }
    handleIncrement = (counter) => {
        const counterIncrement = [...this.state.counters];
        counterIncrement[0] = {...counter};
        counterIncrement[0].value++;
        console.log($this.state.counterIncrement[0]);
        //this.setState({ counters: counterIncrement });
    }
    handleDelete = (counterId) => {
        const counters = this.state.counters.filter( c => c.id !== counterId);
        this.setState({ counters: counters });
    }

    render() { 
        return ( 
        <div>
            <button onClick = { this.handleReset } className="btn btn-primary btn-small m-2">Reset</button>
            { this.state.counters.map(
                counter => 
                <Counter 
                key={counter.id} 
                onIncrement= { this.handleIncrement }
                onDelete={ this.handleDelete } 
                counter = {counter}>
                </Counter>
            )}
        </div> );
    }
}

export default Counters;
